mapper.select:
SELECT id,title,teacher_id,start_time,end_time,create_time,total_subjective,total_choice
FROM exam
WHERE is_deleted=0 AND teacher_id=#{teacherId}

    AND title LIKE '%${wd}%'


    LIMIT #{start},#{limit}

SELECT id,title,teacher_id,start_time,end_time,create_time,total_subjective,total_choice FROM exam WHERE is_deleted=0 AND teacher_id=#{teacherId} AND now()>start_time
AND title LIKE '%${wd}%'
LIMIT #{start},#{limit}

SELECT count(*) FROM exam
WHERE is_deleted=0 AND teacher_id=#{teacherId}

    AND title LIKE '%${wd}%'

mapper.update:
UPDATE exam
SET title=#{title},teacher_id=#{teacherId},start_time=#{startTime},end_time=#{endTime}
WHERE id=#{id}
AND is_deleted=0
